       \"suffix\",\n                \"full_street_name\",\n                \"min_address\",\n                \"max_address\",\n                \"street\",\n                \"suffix_direction\"\n              ],\n            \"columns_datatype\" :\n              [ \"Text\", \"Text\", \"Text\", \"Number\", \"Number\", \"Text\", \"Text\" ],\n            \"columns_description\" : [ \"\", \"\", \"\", \"\", \"\", \"\", \"\" ],\n            \"columns_format\" :\n              [\n                {},\n                { \"align\" : \"left\" },\n                { \"align\" : \"left\", \"aggregate\" : \"count\" },\n                {\n                  \"precisionStyle\" : \"standard\",\n                  \"noCommas\" : \"true\",\n                  \"align\" : \"right\"\n                },\n                {\n                  \"precisionStyle\" : \"standard\",\n                  \"noCommas\" : \"true\",\n                  \"align\" : \"right\"\n                },\n                {},\n                {}\n              ],\n            \"download_count\" : 20303,\n            \"provenance\" : \"official\",\n            \"lens_view_type\" : \"tabular\",\n            \"lens_display_type\" : \"table\",\n            \"locked\" : false,\n            \"blob_mime_type\" : null,\n            \"hide_from_data_json\" : false,\n            \"publication_date\" : \"2012-05-30T22:29:43.000Z\"\n          },\n        \"classification\" :\n          {\n            \"categories\" : [],\n            \"tags\" : [],\n            \"domain_category\" : \"Transportation\",\n            \"domain_tags\" : [ \"gis\", \"streets\" ],\n            \"domain_metadata\" :\n              [\n                { \"key\" : \"Metadata_Frequency\", \"value\" : \"\" },\n                { \"key\" : \"Metadata_Data-Owner\", \"value\" : \"\" },\n                { \"key\" : \"Metadata_Time-Period\", \"value\" : \"\" }\n              ]\n          },\n        \"metadata\" : { \"domain\" : \"data.cityofchicago.org\" },\n        \"permalink\" : \"https://data.cityofchicago.org/d/i6bp-fvbx\",\n        \"link\" : \"https://data.cityofchicago.org/Transportation/Chicago-Street-Names/i6bp-fvbx\",\n        \"owner\" :\n          {\n            \"id\" : \"scy9-9wg4\",\n            \"user_type\" : \"interactive\",\n            \"display_name\" : \"cocadmin\"\n          },\n        \"creator\" :\n          {\n            \"id\" : \"scy9-9wg4\",\n            \"user_type\" : \"interactive\",\n            \"display_name\" : \"cocadmin\"\n          }\n      },\n      {\n        \"resource\" :\n          {\n            \"name\" : \"Transportation Network Providers - Trips (2018 - 2022)\",\n            \"id\" : \"m6dm-c72p\",\n            \"resource_name\" : null,\n            \"parent_fxf\" : [],\n            \"description\" : \"<b>This dataset ends with 2022. Please see the Featured Content link below for the dataset that starts in 2023.</b>\\r\\n\\r\\nAll trips, from November 2018 to December 2022, reported by Transportation Network Providers (sometimes called rideshare companies) to the City of Chicago as part of routine reporting required by ordinance.\\r\\n\\r\\nCensus Tracts are suppressed in some cases, and times are rounded to the nearest 15 minutes. Fares are rounded to the nearest $2.50 and tips are rounded to the nearest $1.00.\\r\\n\\r\\nFor a discussion of the approach to privacy in this dataset, please see https://data.cityofchicago.org/stories/s/82d7-i4i2.\",\n            \"attribution\" : \"City of Chicago\",\n            \"attribution_link\" : \"https://www.chicago.gov\",\n            \"contact_email\" : null,\n            \"type\" : \"dataset\",\n            \"updatedAt\" : \"2025-02-27T22:25:47.000Z\",\n            \"createdAt\" : \"2018-10-02T15:31:29.000Z\",\n            \"metadata_updated_at\" : \"2025-02-27T22:25:47.000Z\",\n            \"data_updated_at\" : \"2023-03-29T22:57:23.000Z\",\n            \"page_views\" :\n              {\n                \"page_views_last_week\" : 19